import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FactDisplay.css';

const FactDisplay = ({ articles, error, isLoading }) => {
  const [expandedArticleIndex, setExpandedArticleIndex] = useState(null);
  const [additionalData, setAdditionalData] = useState({});
  const [loadingAdditionalData, setLoadingAdditionalData] = useState({});

  useEffect(() => {
    if (expandedArticleIndex !== null && !loadingAdditionalData[expandedArticleIndex]) {
      setLoadingAdditionalData((prevLoading) => ({
        ...prevLoading,
        [expandedArticleIndex]: true,
      }));
    }
  }, [expandedArticleIndex]);

  const handleToggleExpand = async (index, article) => {
    if (expandedArticleIndex === index) {
      setExpandedArticleIndex(null);
    } else {
      setExpandedArticleIndex(index);
      try {
        const response = await axios.get('http://localhost:8000/additional-data', {
          params: {
            articleId: index,
            articleUrl: article.url,
          },
        });
        const { summary, biasRating } = response.data;
        let summaryText = '';
        let biasRatingText = '';
        setAdditionalData((prevData) => ({
          ...prevData,
          [index]: { summary: '', biasRating: '' },
        }));
        for (let i = 0; i < summary.length; i++) {
          summaryText += summary[i];
          setAdditionalData((prevData) => ({
            ...prevData,
            [index]: { summary: summaryText, biasRating: biasRatingText },
          }));
          await new Promise((resolve) => setTimeout(resolve, 50)); // Simulate typing effect
        }
        for (let i = 0; i < biasRating.length; i++) {
          biasRatingText += biasRating[i];
          setAdditionalData((prevData) => ({
            ...prevData,
            [index]: { summary: summaryText, biasRating: biasRatingText },
          }));
          await new Promise((resolve) => setTimeout(resolve, 50)); // Simulate typing effect
        }
      } catch (err) {
        console.error('Error fetching additional data:', err);
      } finally {
        setLoadingAdditionalData((prevLoading) => ({
          ...prevLoading,
          [index]: false,
        }));
      }
    }
  };

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  if ((!articles || articles.length === 0) && !isLoading) {
    return <div className="no-facts">Search something...</div>;
  }

  if (isLoading) {
    return <div className="no-facts">Loading...</div>;
  }

  return (
    <div className="fact-display">
      <h3>Key Facts:</h3>
      <ul>
        {articles.map((article, index) => (
          <li key={index} className="fact-item" onClick={() => handleToggleExpand(index, article)}>
            <h4>{article.title}</h4>
            <p>{article.description}</p>

            <div
              className={`expanded-content ${expandedArticleIndex === index ? 'expanded' : ''}`}
              style={{
                maxHeight: expandedArticleIndex === index ? 'none' : '0',
                overflow: 'hidden',
                transition: 'max-height 0.3s ease-in-out',
              }}
            >
              {loadingAdditionalData[index] && expandedArticleIndex === index ? (
                <p>Loading additional information...</p>
              ) : (
                expandedArticleIndex === index && additionalData[index] && (
                  <>
                    <p>AI Summary: {additionalData[index].summary}</p>
                    <p>Bias Rating: {additionalData[index].biasRating}</p>
                  </>
                )
              )}
            </div>
            <a href={article.url} target="_blank" rel="noopener noreferrer">
              Read full article
            </a>
            <p>Source: {article.source}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FactDisplay;
