import React, { useState } from 'react';
import { fetchFacts } from './api';
import SearchBar from './components/SearchBar';
import FactDisplay from './components/FactDisplay';

const App = () => {
  const [articles, setArticles] = useState([]);
  const [error, setError] = useState(null);

  const handleSearch = async (topic, dateRange) => {
    setArticles([]);
    setError(null);
    try {
      const data = await fetchFacts(topic, dateRange);
      setArticles(data.articles);
    } catch (err) {
      setError('Unable to fetch facts. Please try again.');
    }
  };

  return (
    <div>
      <h1 style={{ textAlign: 'center', color: '#333', margin: '20px 0' }}>News Fact Checker</h1>
      <SearchBar onSearch={handleSearch} />
      <FactDisplay articles={articles} error={error} />
    </div>
  );
};

export default App;
