import axios from 'axios';

const BASE_URL = 'localhost:8000';

export const fetchFacts = async (topic) => {
  try {
    const response = await axios.get(`${BASE_URL}/facts`, { params: { topic } });
    return response.data;
  } catch (error) {
    console.error("Error fetching facts:", error);
    throw error;
  }
};
